import React from "react";
import Meal from "./Meal";


export default function MealList({ mealData }) {
    const nutrients = mealData.nutrients
    const { calories,carbohydrates,fat,protein } = nutrients
    console.log(mealData);

    // handleAddRecipe = (R,C,F,P) => {
    //     localStorage.Recipe = [R.toFix(0), C.toFix(0), F.toFixed(0), P.toFixed(0)];
    //     alert('成功！');
    // }

    function handleAddRecipe(e) {
        console.log(e);
        alert('成功！');
        localStorage.recipe = [e];
        // localStorage.recipe = [R.toFix(0), C.toFix(0), F.toFixed(0), P.toFixed(0)];

    }

    return (
        <main>
            <section className="nutrients">
                <h1>Macros</h1>
                <ul>
                    <li>Calories: {calories.toFixed(0)}</li>
                    <li>Carbohydrates: {carbohydrates.toFixed(0)}</li>
                    <li>Fat: {fat.toFixed(0)}</li>
                    <li>Protein: {protein.toFixed(0)}</li>
                </ul>
                <button onClick={() => handleAddRecipe([calories,protein,fat,carbohydrates])}>Add</button>
            </section>
            <section className="meals">
                {mealData.meals.map((meal) => {
                    return <Meal key={meal.id} meal={meal} />
                })}
            </section>
        </main>
    )
}
