import React from "react";
import Meal from "./Meal";

export default function MealList({ mealData }) {
    // state = {
    //     recipeCalorie: '',
    //     recipeCarbohydrate: '',
    //     recipeFat: '',
    //     recipeProtein: '',
    // }
    const nutrients = mealData.nutrients
    console.log(mealData);

    return (
        <main>
            <section className="nutrients">
                <h1>Macros</h1>
                <ul>
                    <li>Calories: {nutrients.calories.toFixed(0)}</li>
                    <li>Carbohydrates: {nutrients.carbohydrates.toFixed(0)}</li>
                    <li>Fat: {nutrients.fat.toFixed(0)}</li>
                    <li>Protein: {nutrients.protein.toFixed(0)}</li>
                </ul>
                <main>
	<input type="checkbox" id="i" />

	<label for="i" class="checkbox">
		<div class="checkbox__inner">
			<div class="green__ball"></div>
		</div>
	</label>
	<div class="checkbox__text">
		<span>Decide to</span>
		<div class="checkbox__text--options">
			<span class="off">noteat</span>
			<span class="on">eat</span>
		</div>
	</div>
</main>



<div class="support">
	<a href="https://twitter.com/DevLoop01" target="_blank"><i class="fab fa-twitter-square"></i></a>
	<a href="https://dribbble.com/devloop01" target="_blank"><i class="fab fa-dribbble"></i></a>
</div>
            </section>
            <section className="meals">
                {mealData.meals.map((meal) => {
                    return <Meal key={meal.id} meal={meal} />
                })}
            </section>
        </main>
    )
}
